import React, { useState } from 'react';
import Navigation from "../../Navigation/Navigation";
import Classes from "../Login/Login.css"


const Login = () => {

    const [details, setDetails] = useState({ email: "", password: "" });
    const [error, setError] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();

        const postRequest = details;

        fetch('http://localhost:5000/auth/login', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(postRequest)
        }).then((response) => response.json())
            .then((responseData) => {
                console.log(responseData);
                return responseData;
            }).catch((error) => {
                console.log(error.message);
                setError(error.message);
            })
    }

    return (
        <div>
            <Navigation></Navigation>
            <h1>Login</h1>
            {<div>{ }</div>}
            <form onSubmit={handleSubmit}>
                <label for="email">E-mail</label>
                <input id="email" type="text" onChange={e => setDetails({ ...details, email: e.target.value })} value={details.email} /> <br />
                <label for="password">Password</label>
                <input id="password" type="password" onChange={e => setDetails({ ...details, password: e.target.value })} value={details.password} /><br />
                <button type="submit">Login</button>
            </form>
        </div >
    )

}

export default Login;
