import React, { useEffect, useState } from "react";
import Navigation from "../../Navigation/Navigation";
import Classes from "../Monthly/Monthly.css";
import AddExpense from "../Add Expense/Add";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Item from "../../UI/Table/Items";

const Monthly = () => {
  const [data, setData] = useState();
  let token = JSON.parse(localStorage.userDetails).token;

  useEffect(() => {
    fetch(`http://localhost:5000/plan/2017/10`, {
      method: "GET",
      headers: { Authorization: `bearer ${token}` },
    })
      .then((response) => response.json())
      .then((responseData) => {
        setData(responseData);
      })
      .catch((error) => console.log(error.message));
  }, []);

  return (
    <div>
      <Navigation></Navigation>
      <h1>Monthly balance</h1>
      <div className="card-monthly">
        <div className="planner">
          <h1>Planner</h1>
          <label>Income</label>
          <input class="form-control" type="text" />
          <label>Budget:</label>
          <input class="form-control" type="text" />
          <button type="submit">Save</button>
        </div>
        <div className="expenses">
          <h1>November 2017</h1>
          <div className="row">
            <h2>Expenses</h2>
            <Link to="/addexpense" className="link-2">
              Add expense
            </Link>
          </div>
          <table className="table">
            <thead>
              <th>Name</th>
              <th>Category</th>
              <th>Cost</th>
              <th>Payment date</th>
            </thead>
            <tbody>
              {/* {data.expenses.map((el) => (
                <Item
                  name={el.name}
                  category={el.category}
                  amount={el.amount}
                  date={el.date}
                />
              ))} */}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Monthly;
