import React from "react";
import Classes from "../Card/Card.css";

const Card = (props) => {
  return (
    <div className="card">
      <h1>{props.month}</h1>
      <h3>{props.year}</h3>
      <label>Budget:</label>
      <input name="budget" placeholder={props.budget} disabled></input>
      <label>Balance:</label>
      <input name="budget" placeholder={props.balance} disabled></input>
      <Link
        to={{
          pathname: "/courses",
          search: "?sort=name",
          hash: "#the-hash",
          state: { fromDashboard: true },
        }}
      />
    </div>
  );
};

export default Card;
