import React, { useEffect, useState } from "react";
import Navigation from "../../Navigation/Navigation";
import Classes from "../Add Expense/Add.css";
import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";
import "react-notifications/lib/notifications.css";

const AddExpense = () => {
  let token = JSON.parse(localStorage.userDetails).token;
  let url = window.location.href;
  const month = url.charAt(url.length - 1);
  const [response, setResponse] = useState(false);

  const createNotification = (type, message) => {
    return () => {
      switch (type) {
        case "success":
          NotificationManager.success("Success", message);
          break;
        case "error":
          NotificationManager.error(message, "Click me!", 5000, () => {
            alert("callback");
          });
          break;
      }
    };
  };

  useEffect(() => {
    response
      ? createNotification("success", "Successfulllyyy")
      : createNotification("error", "Greshka");
  }, [response]);

  const [details, setDetails] = useState({
    date: 0,
    name: "",
    category: "",
    amount: 0,
  });

  const postExpense = (e) => {
    e.preventDefault();

    Object.keys(details).forEach((e) => {
      if (isNaN(details[e]) === false) {
        details[e] = parseInt(details[e]);
      }
    });

    fetch(`http://localhost:5000/plan/2017/${month}/expense`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `bearer ${token}`,
      },
      body: JSON.stringify(details),
    })
      .then((response) => response.json())
      .then((responseData) => {
        setResponse(responseData.success);
        console.log(response);
      })
      .catch((error) => {
        console.log(error.message);
      });
  };

  return (
    <div>
      <Navigation></Navigation>
      <h1>Add Expenses</h1>
      <form onSubmit={postExpense}>
        <h3>Add a new expense</h3>
        <label>Name:</label>
        <input
          id="name"
          type="text"
          onChange={(e) => setDetails({ ...details, name: e.target.value })}
          value={details.name}
        />
        <br />
        <label>Category:</label>
        <select
          onChange={(e) => setDetails({ ...details, category: e.target.value })}
          value={details.category}
        >
          <option value="Non-essential">Non-essential</option>
          <option value="Fixed">Fixed</option>
          <option value="Variable">Variable</option>
        </select>
        <br />
        <label type>Cost:</label>
        <input
          id="cost"
          type="number"
          onChange={(e) => setDetails({ ...details, amount: e.target.value })}
          value={details.amount}
        />
        <br />
        <label>Payment date:</label>
        <input
          type="number"
          min="01"
          max="31"
          onChange={(e) => setDetails({ ...details, date: e.target.value })}
          value={details.date}
        />
        <br />
        <button type="submit">Submit</button>
      </form>

      <NotificationContainer />
    </div>
  );
};

export default AddExpense;
