import React from 'react';
import classes from "../Navigation/Navigation.css";
import Login from '../Pages/Login/Login';
import Registration from '../Pages/Registration/Registration';
import { useHistory } from 'react-router-dom';


import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

const Navigation = () => {

    let history = useHistory();

    const logout = () => {
        localStorage.removeItem('userDetails')
        history.push("/login")
    }

    return (
        <div className="navigation">
            {"userDetails" in localStorage ? "" : <Link to="/login" className="link">Login</Link>}
            {"userDetails" in localStorage ? "" : <Link to="/registration" className="link">Registration</Link>}
            {"userDetails" in localStorage ? <Link to="/monthly" className="link">Monthly Balance</Link> : ""}
            {"userDetails" in localStorage ? <Link to="/yearly" className="link">Yearly Balance</Link> : ""}
            {"userDetails" in localStorage ? <button onClick={logout}> Logout</button> : ""}
        </div >
    )
}

export default Navigation;
