import React, { useEffect, useState } from "react";
import Navigation from "../../Navigation/Navigation";
import Classes from "../Monthly/Monthly.css";
import AddExpense from "../Add Expense/Add";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Item from "../../UI/Table/Items";

const Monthly = () => {
  const [expenses, setExpenses] = useState();
  const [money, setMoney] = useState({ income: 5, budget: 5 });
  let token = JSON.parse(localStorage.userDetails).token;
  let numbers = Object.keys(money).map((key) => {
    return money[key];
  });
  console.log(numbers);

  //   useEffect(() => {
  //     fetch(`http://localhost:5000/plan/2017/10`, {
  //       method: "GET",
  //       headers: { Authorization: `bearer ${token}` },
  //     })
  //       .then((response) => response.json())
  //       .then((responseData) => {
  //         setData(responseData);
  //       })
  //       .catch((error) => console.log(error.message));
  //   }, []);

  const postData = (e) => {
    e.preventDefault();
    console.log(money);
    fetch(`http://localhost:5000/plan/2018/10`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `bearer ${token}`,
      },
      body: money,
    })
      .then((response) => response.json())
      .then((responseData) => {
        console.log(responseData.message);
      });
  };

  return (
    <div>
      <Navigation></Navigation>
      <h1>Monthly balance</h1>
      <div className="card-monthly">
        <div className="planner">
          <h1>Planner</h1>
          <form onSubmit={postData}>
            <label>Income</label>
            <input
              type="number"
              onChange={(e) => setMoney({ ...money, income: e.target.value })}
              value={money.income}
            />
            <label>Budget:</label>
            <input
              type="number"
              onChange={(e) => setMoney({ ...money, budget: e.target.value })}
              value={money.budget}
            />
            <button type="submit">Save</button>
          </form>
        </div>
        <div className="expenses">
          <h1>November 2017</h1>
          <div className="row">
            <h2>Expenses</h2>
            <Link to="/addexpense" className="link-2">
              Add expense
            </Link>
          </div>
          <table className="table">
            <thead>
              <th>Name</th>
              <th>Category</th>
              <th>Cost</th>
              <th>Payment date</th>
            </thead>
            <tbody>
              {/* {data.expenses.map((el) => (
                <Item
                  name={el.name}
                  category={el.category}
                  amount={el.amount}
                  date={el.date}
                />
              ))} */}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Monthly;
