import React, { useState } from 'react';
import Navigation from '../../Navigation/Navigation';
import classes from "../Registration/Registration.css";

const Registration = () => {

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const success = {
        "success": true,
        "message": "You have successfully signed up! Now you should be able to log in"
    }

    const noSuccess = {
        "success": false,
        "message": "E-mail already exists!"
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const postRequest = { name, email, password };

        fetch('http://localhost:5000/auth/signup', {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(postRequest)
        }).then(response => {
            if (response.ok) return success.json();
        }).catch((response) => {
            if (!response.ok) return noSuccess.json();
        })
    }

    return (
        <div>
            <Navigation></Navigation>
            <h1>Registration</h1>
            <form onSubmit={handleSubmit}>
                <label>Username</label>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} /> <br />

                <label class="form-control-label" for="new-email">E-mail</label>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} /> <br />

                <label class="form-control-label" for="new-password">Password</label>
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} /> <br />

                <button type="submit">Submit</button>
            </form>
        </div>
    )
}

export default Registration;
