import React, { useEffect, useState } from "react";
import Navigation from "../../Navigation/Navigation";
import Card from "../../UI/Card/Card";
import Classes from "../Yearly/Yearly.css";

const monthsOfYear = [
  "January",
  "February",
  "March",
  "April",
  "May",
  "June",
  "July",
  "August",
  "September",
  "October",
  "November",
  "December",
];

const Yearly = () => {
  const [months, setMonths] = useState([]);

  let token = JSON.parse(localStorage.userDetails).token;

  useEffect(() => {
    fetch("http://localhost:5000/plan/year", {
      method: "GET",
      headers: { Authorization: `bearer ${token}` },
    })
      .then((response) => response.json())
      .then((responseData) => {
        const keys = Object.values(responseData);
        const newKeys = keys.map((el, i) => ({ ...el, month: i, year: 2017 }));
        return newKeys;
      })
      .catch((error) => console.log(error.message));
  }, []);

  return (
    <div>
      <Navigation></Navigation>
