import React, { useState } from "react";
import Navigation from "../../Navigation/Navigation";
import Classes from "../Add Expense/Add.css";

const AddExpense = () => {
  let token = JSON.parse(localStorage.userDetails).token;

  const [details, setDetails] = useState({
    date: 0,
    name: "",
    category: "",
    amount: 0,
  });

  const postExpense = (e) => {
    e.preventDefault();

    Object.keys(details).forEach((e) => {
      if (isNaN(details[e]) === false) {
        details[e] = parseInt(details[e]);
      }
    });

    console.log(details);
    fetch(`http://localhost:5000/plan/2017/1/expense`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `bearer ${token}`,
      },
      body: JSON.stringify(details),
    })
      .then((response) => response.json())
      .then((responseData) => {
        console.log(responseData.message);
      })
      .catch((error) => console.log(error.message));
  };

  return (
    <div>
      <Navigation></Navigation>
      <h1>Add Expenses</h1>
      <form onSubmit={postExpense}>
        <h3>Add a new expense</h3>
        <label>Name:</label>
        <input
          id="name"
          type="text"
          onChange={(e) => setDetails({ ...details, name: e.target.value })}
          value={details.name}
        />
        <br />
        <label>Category:</label>
        <select
          onChange={(e) => setDetails({ ...details, category: e.target.value })}
          value={details.category}
        >
          <option value="Non-essential">Non-essential</option>
          <option value="Fixed">Fixed</option>
          <option value="Variable">Variable</option>
        </select>
        <br />
        <label type>Cost:</label>
        <input
          id="cost"
          type="number"
          onChange={(e) => setDetails({ ...details, amount: e.target.value })}
          value={details.amount}
        />
        <br />
        <label>Payment date:</label>
        <input
          number="date"
          onChange={(e) => setDetails({ ...details, date: e.target.value })}
          value={details.date}
        />
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddExpense;
